import React from 'react'
import { useState } from 'react';
import './CSS/style.css'
import { MdRestaurantMenu } from 'react-icons/md';
import { BsCart4 } from 'react-icons/bs';


const Menu = () => {

    const response = require("./data.json");
    const data = response.food
    let cartData = [
        {
            "name": "Product",
            "price": "Rate",
            "quantity": "Quantity",
            "net": "Price"
        }
    ]
    const [Arr, setArr] = useState(cartData)




    return (
        <>

            <div className="cont">
                <div className="menu-list">
                    <h2><MdRestaurantMenu />&nbsp;Menu</h2>


                    <div className="menu-cont">
                        {
                            data.map((ele) => {
                                return (
                                    <div className="card" key={ele.id}>
                                        <div className="img">
                                            <img src={ele.image} alt="salad" />
                                        </div>
                                        <p className="details">
                                            {ele.name} <br />
                                            <span className="price">$&nbsp;{ele.price}</span>
                                        </p>
                                        {ele.quantity > 0 ?
                                            <button className='add2cart'
                                                onClick={() => {
                                                    ele.quantity=ele.quantity+1
                                                    let obj = {
                                                        "name": ele.name,
                                                        "price": ele.price,
                                                        "quantity": ele.quantity,
                                                        "net": ele.price*ele.quantity
                                                    }
                                                    setArr(...Arr, Arr.push())
                                                }}>Add to cart</button> :
                                            <div className='incDec'>
                                                <button className='dec'>-</button>
                                                <span>num</span>
                                                <button className='inc'>+</button>
                                            </div>
                                        }

                                    </div>

                                )
                            })
                        }



                    </div>
                </div>

                <div className="cart">
                    <h2><BsCart4 />&nbsp;Cart</h2>

                    {1 == 2 ?
                        <p>No item in cart</p> :
                        <p>X items in cart</p>}
                </div>

            </div>
        </>
    )
}

export default Menu
