import React from 'react'
import { useState } from 'react';
import './CSS/style.css'
import { MdRestaurantMenu } from 'react-icons/md';
import { BsCart4 } from 'react-icons/bs';


const Menu = () => {

    const response = require("./data.json");
    const data = response.food
    const [Arr, setArr] = useState([])




    return (
        <>

            <div className="cont">
                <div className="menu-list">
                    <h2><MdRestaurantMenu />&nbsp;Menu</h2>


                    <div className="menu-cont">
                        {
                            data.map((ele) => {
                                return (
                                    <div className="card" key={ele.id}>
                                        <div className="img">
                                            <img src={ele.image} alt="salad" />
                                        </div>
                                        <p className="details">
                                            {ele.name} <br />
                                            <span className="price">$&nbsp;{ele.price}</span>
                                        </p>


                                        <button className='add2cart'
                                            onClick={() => {
                                                let obj = {
                                                    "id": ele.id,
                                                    "name": ele.name,
                                                    "price": ele.price,
                                                    "quantity": ele.quantity + 1,
                                                    "net": ele.price * (ele.quantity + 1)
                                                }
                                                setArr(Arr => [...Arr, obj])

                                            }}>Add to cart</button>
                                    </div>

                                )
                            })
                        }



                    </div>
                </div>

                <div className="cart">
                    <h2><BsCart4 />&nbsp;Cart</h2>

                    {Arr.length===0 ?
                        <p>No item in cart</p> :
                        <>{
                            Arr.map((i) => {
                                return (
                                    <div className="cart-cont">
                                        <div className="cart-list-title">
                                            <span>ID</span>
                                            <span>Name</span>
                                            <span>Rate</span>
                                            <span>Quantity</span>
                                            <span>Price</span>
                                            <span></span>

                                        </div>
                                        <div className="cart-list">
                                            <span>{i.id} </span>
                                            <span>Name</span>
                                            <span>Rate</span>
                                            <span>Quantity</span>
                                            <span>Price</span>
                                            <span></span>

                                        </div>
                                    </div>

                                )
                            }

                            )
                        }</>}
                </div>

            </div>
        </>
    )
}

export default Menu
